# include <string.h># include "Engine/CoreEngine.hpp"# include "GKrellMProgram.hpp"# include <stdlib.h># include <time.h>int		main( int argc, char *argv[] ){	srand( time ( 0 ) );	Program *program = new GKrellMProgram();	CoreEngine *engine = new CoreEngine( 60, *program );	int		win = 0;	int		i = 0;	while ( i < argc )	{		if ( strlen( argv[i] ) >= 2 && argv[i][0] == '-' )		{			if (  argv[i][1] == 'n' )				win |= NCURSES;			else if ( argv[i][1] == 'g' )				win |= GRAPHIC;		}		i++;	}	engine->createWindow( win );	engine->start();	delete engine;	return ( 0 );}